import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';

const CARD_VALUES = ['1', '2', '3', '5', '8', '13', '21', '34', '?'];

function Room() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const { username, isModerator } = location.state || {};

  const [socket, setSocket] = useState(null);
  const [users, setUsers] = useState([]);
  const [taskId, setTaskId] = useState('');
  const [selectedCard, setSelectedCard] = useState(null);
  const [showResults, setShowResults] = useState(false);
  const [votes, setVotes] = useState({});
  const [finalScore, setFinalScore] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!username) {
      navigate('/');
      return;
    }

    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);

    if (isModerator) {
      newSocket.emit('createRoom', { roomId, moderator: username });
    } else {
      newSocket.emit('joinRoom', { roomId, username });
    }

    newSocket.on('roomUpdate', (roomData) => {
      setUsers(roomData.users);
    });

    newSocket.on('voteUpdate', (voteData) => {
      setVotes(voteData);
    });

    newSocket.on('results', (result) => {
      setVotes(result.votes);
      setFinalScore(result.finalScore);
      setShowResults(true);
    });

    newSocket.on('roomError', (error) => {
      setError(error.message);
      setTimeout(() => {
        navigate('/');
      }, 3000);
    });

    newSocket.on('roomStatus', (status) => {
      if (status.exists && !status.isModerator && isModerator) {
        setError('Bu oda zaten var ve başka bir moderatör tarafından yönetiliyor');
        setTimeout(() => {
          navigate('/');
        }, 3000);
      }
    });

    return () => newSocket.close();
  }, [roomId, username, isModerator, navigate]);

  const handleVote = (value) => {
    setSelectedCard(value);
    socket.emit('vote', { roomId, vote: value });
  };

  const handleShowResults = () => {
    if (!taskId.trim()) {
      setError('Lütfen bir Task ID girin');
      return;
    }
    socket.emit('showResults', { roomId, taskId });
  };

  const startNewVoting = () => {
    if (!taskId.trim()) {
      setError('Lütfen bir Task ID girin');
      return;
    }
    setShowResults(false);
    setSelectedCard(null);
    setVotes({});
    setFinalScore(null);
    setError(null);
  };

  if (error) {
    return (
      <div className="max-w-md mx-auto mt-10 p-4 bg-red-100 border border-red-400 text-red-700 rounded">
        <p>{error}</p>
        <p className="text-sm mt-2">Ana sayfaya yönlendiriliyorsunuz...</p>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      <div className="bg-white rounded-xl shadow-md p-6 mb-6">
        <h2 className="text-2xl font-bold mb-4">Oda: {roomId}</h2>

        {isModerator && (
          <div className="mb-6">
            <input
              type="text"
              value={taskId}
              onChange={(e) => {
                setTaskId(e.target.value);
                setError(null);
              }}
              placeholder="Jira Task ID"
              className={`mr-2 px-3 py-2 border rounded ${error && !taskId.trim() ? 'border-red-500' : ''}`}
            />
            <button
              onClick={startNewVoting}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
            >
              Yeni Oylama Başlat
            </button>
            {error && !taskId.trim() && (
              <p className="text-red-500 text-sm mt-1">{error}</p>
            )}
          </div>
        )}

        <div className="grid grid-cols-3 gap-4 mb-6">
          <div className="col-span-2">
            <h3 className="text-lg font-semibold mb-2">Kartlar</h3>
            <div className="grid grid-cols-3 gap-2">
              {CARD_VALUES.map((value) => (
                <button
                  key={value}
                  onClick={() => handleVote(value)}
                  className={`p-4 text-center border rounded transition-colors ${
                    selectedCard === value 
                      ? 'bg-blue-500 text-white' 
                      : 'hover:bg-gray-100'
                  }`}
                  disabled={showResults}
                >
                  {value}
                </button>
              ))}
            </div>
          </div>

          <div>
            <h3 className="text-lg font-semibold mb-2">Katılımcılar</h3>
            <ul className="space-y-2">
              {users.map((user) => (
                <li key={user.id} className="flex items-center justify-between">
                  <span>{user.name}</span>
                  {votes[user.id] && !showResults && (
                    <span className="text-green-500">✓</span>
                  )}
                  {showResults && (
                    <span className="font-bold">{votes[user.id]}</span>
                  )}
                </li>
              ))}
            </ul>
          </div>
        </div>

        {showResults && (
          <div className="mt-6 p-4 bg-gray-50 rounded-lg">
            <h3 className="text-lg font-semibold mb-2">Oylama Sonucu</h3>
            <p className="text-2xl font-bold text-blue-600">{finalScore} Puan</p>
          </div>
        )}

        {isModerator && Object.keys(votes).length === users.length && !showResults && (
          <button
            onClick={handleShowResults}
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Sonuçları Göster
          </button>
        )}
      </div>
    </div>
  );
}

export default Room;
