import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import io from 'socket.io-client';

const CARD_VALUES = ['1', '2', '3', '5', '8', '13', '21', '34', '?'];

function Room() {
  const { roomId } = useParams();
  const location = useLocation();
  const { username, isModerator } = location.state || {};

  const [socket, setSocket] = useState(null);
  const [users, setUsers] = useState([]);
  const [taskId, setTaskId] = useState('');
  const [selectedCard, setSelectedCard] = useState(null);
  const [showResults, setShowResults] = useState(false);
  const [votes, setVotes] = useState({});

  useEffect(() => {
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);

    if (isModerator) {
      newSocket.emit('createRoom', { roomId, moderator: username });
    } else {
      newSocket.emit('joinRoom', { roomId, username });
    }

    newSocket.on('roomUpdate', (roomData) => {
      setUsers(roomData.users);
    });

    newSocket.on('voteUpdate', (voteData) => {
      setVotes(voteData);
    });

    newSocket.on('results', (result) => {
      setVotes(result.votes);
      setShowResults(true);
    });

    return () => newSocket.close();
  }, [roomId, username, isModerator]);

  const handleVote = (value) => {
    setSelectedCard(value);
    socket.emit('vote', { roomId, vote: value });
  };

  const handleShowResults = () => {
    socket.emit('showResults', { roomId });
  };

  const startNewVoting = () => {
    setShowResults(false);
    setSelectedCard(null);
    setVotes({});
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <div className="bg-white rounded-xl shadow-md p-6 mb-6">
        <h2 className="text-2xl font-bold mb-4">Oda: {roomId}</h2>

        {isModerator && (
          <div className="mb-6">
            <input
              type="text"
              value={taskId}
              onChange={(e) => setTaskId(e.target.value)}
              placeholder="Jira Task ID"
              className="mr-2 px-3 py-2 border rounded"
            />
            <button
              onClick={startNewVoting}
              className="bg-blue-500 text-white px-4 py-2 rounded"
            >
              Yeni Oylama Başlat
            </button>
          </div>
        )}

        <div className="grid grid-cols-3 gap-4 mb-6">
          <div className="col-span-2">
            <h3 className="text-lg font-semibold mb-2">Kartlar</h3>
            <div className="grid grid-cols-3 gap-2">
              {CARD_VALUES.map((value) => (
                <button
                  key={value}
                  onClick={() => handleVote(value)}
                  className={`p-4 text-center border rounded ${
                    selectedCard === value ? 'bg-blue-500 text-white' : 'hover:bg-gray-100'
                  }`}
                >
                  {value}
                </button>
              ))}
            </div>
          </div>

          <div>
            <h3 className="text-lg font-semibold mb-2">Katılımcılar</h3>
            <ul className="space-y-2">
              {users.map((user) => (
                <li key={user.id} className="flex items-center">
                  <span>{user.name}</span>
                  {votes[user.id] && !showResults && (
                    <span className="ml-2 text-green-500">✓</span>
                  )}
                  {showResults && (
                    <span className="ml-2 font-bold">{votes[user.id]}</span>
                  )}
                </li>
              ))}
            </ul>
          </div>
        </div>

        {isModerator && Object.keys(votes).length === users.length && !showResults && (
          <button
            onClick={handleShowResults}
            className="bg-green-500 text-white px-4 py-2 rounded"
          >
            Sonuçları Göster
          </button>
        )}
      </div>
    </div>
  );
}

export default Room;
