import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Home() {
  const [roomId, setRoomId] = useState('');
  const [username, setUsername] = useState('');
  const [isCreating, setIsCreating] = useState(true);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (roomId && username) {
      navigate(`/room/${roomId}`, { 
        state: { 
          username,
          isModerator: isCreating 
        } 
      });
    }
  };

  return (
    <div className="max-w-md mx-auto bg-white rounded-xl shadow-md overflow-hidden md:max-w-2xl p-6">
      <h1 className="text-2xl font-bold text-center mb-6">Scrum Poker</h1>

      <div className="flex justify-center space-x-4 mb-6">
        <button
          className={`px-4 py-2 rounded ${isCreating ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          onClick={() => setIsCreating(true)}
        >
          Oda Oluştur
        </button>
        <button
          className={`px-4 py-2 rounded ${!isCreating ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          onClick={() => setIsCreating(false)}
        >
          Odaya Katıl
        </button>
      </div>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700">
            {isCreating ? 'Oda ID Oluştur' : 'Oda ID'}
          </label>
          <input
            type="text"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            required
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Kullanıcı Adı
          </label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            required
          />
        </div>

        <button
          type="submit"
          className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
        >
          {isCreating ? 'Oda Oluştur' : 'Odaya Katıl'}
        </button>
      </form>
    </div>
  );
}

export default Home;
