import React from 'react';

function LanguageSelector({ currentLang, onLanguageChange }) {
  return (
    <div className="fixed top-4 right-4 flex gap-2">
      <button
        onClick={() => onLanguageChange('tr')}
        className={`w-10 h-10 rounded-full overflow-hidden border-2 transition-transform hover:scale-110 ${
          currentLang === 'tr' ? 'border-white' : 'border-transparent'
        }`}
      >
        <img
          src="https://flagcdn.com/w40/tr.png"
          alt="Türkçe"
          className="w-full h-full object-cover"
        />
      </button>
      <button
        onClick={() => onLanguageChange('en')}
        className={`w-10 h-10 rounded-full overflow-hidden border-2 transition-transform hover:scale-110 ${
          currentLang === 'en' ? 'border-white' : 'border-transparent'
        }`}
      >
        <img
          src="https://flagcdn.com/w40/gb.png"
          alt="English"
          className="w-full h-full object-cover"
        />
      </button>
    </div>
  );
}

export default LanguageSelector;
