import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Home() {
  const [roomId, setRoomId] = useState('');
  const [username, setUsername] = useState('');
  const [isCreating, setIsCreating] = useState(true);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (roomId && username) {
      navigate(`/room/${roomId}`, { 
        state: { 
          username,
          isModerator: isCreating 
        } 
      });
    }
  };

  return (
    <div className="max-w-md mx-auto bg-white rounded-xl shadow-md overflow-hidden md:max-w-2xl p-6">
      <h1 className="text-2xl font-bold text-center mb-6">Scrum Poker</h1>

      <div className="relative mb-8">
        {/* Toggle Container */}
        <div className="w-full h-12 bg-gray-200 rounded-lg p-1">
          {/* Sliding Background */}
          <div
            className={`absolute top-1 transition-all duration-300 ease-in-out h-10 w-1/2 bg-blue-500 rounded-md ${
              isCreating ? 'left-1' : 'left-[calc(50%-2px)]'
            }`}
          />

          {/* Button Container */}
          <div className="relative flex h-full">
            <button
              className={`flex-1 transition-colors duration-300 rounded-md font-medium ${
                isCreating ? 'text-white' : 'text-gray-700'
              }`}
              onClick={() => setIsCreating(true)}
            >
              Oda Oluştur
            </button>
            <button
              className={`flex-1 transition-colors duration-300 rounded-md font-medium ${
                !isCreating ? 'text-white' : 'text-gray-700'
              }`}
              onClick={() => setIsCreating(false)}
            >
              Odaya Katıl
            </button>
          </div>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="space-y-1">
          <label className="block text-sm font-medium text-gray-700">
            {isCreating ? 'Oda ID Oluştur' : 'Oda ID'}
          </label>
          <input
            type="text"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
            placeholder={isCreating ? 'Yeni oda ID girin' : 'Var olan oda ID girin'}
            required
          />
        </div>

        <div className="space-y-1">
          <label className="block text-sm font-medium text-gray-700">
            Kullanıcı Adı
          </label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
            placeholder="Kullanıcı adınızı girin"
            required
          />
        </div>

        <button
          type="submit"
          className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-all duration-300"
        >
          {isCreating ? 'Oda Oluştur' : 'Odaya Katıl'}
        </button>
      </form>
    </div>
  );
}

export default Home;
